mapper.select:
select h.aptCode, h.aptName, h.buildyear, h.dongCode, h.dongName, h.jibun, h.lat, h.lng, si.sidoname, gu.gugunname,
		(select replace(concat(trim(dealAmount)), ',', '') from housedeal where aptCode = h.aptCode and no = (select max(no) from housedeal where aptCode = h.aptCode)) recentPrice
from houseinfo h
left join sidocode si
on left(h.dongcode,2) = left(si.sidocode,2)
left join guguncode gu
on left(h.dongcode,5) = left(gu.guguncode,5)
where (select replace(concat(trim(dealAmount)), ',', '') from housedeal where aptCode = h.aptCode and no = (select max(no) from housedeal where aptCode = h.aptCode)) <= #{maxPrice}
and dongCode = #{dong}
order by aptName;

select *
from baseaddress b 
where left (dongCode,5) = #{gugunCode} and dongName = #{dongName};

select *
from seoulstore s 
where dongName = #{dongName} and classes = #{classes};
